import { __decorate } from "tslib";
import { Component } from '@angular/core';
import Swal from 'sweetalert2';
let HomeComponent = class HomeComponent {
    constructor(profileS, homeS, toast, cookie) {
        this.profileS = profileS;
        this.homeS = homeS;
        this.toast = toast;
        this.cookie = cookie;
        this.encabezado = 'Los Mas Buscados';
        this.rol = this.cookie.get('ROL');
        this.favoritos = [];
        this.inmuebles = [];
    }
    ngOnInit() {
        this.obtenerInmuebles();
        this.loadFavorites();
    }
    async obtenerInmuebles() {
        try {
            const data = await this.homeS.getInmuebles();
            this.inmuebles = data;
        }
        catch (error) {
            Swal.fire('Ocurrio un error', undefined, 'error');
        }
    }
    async loadFavorites() {
        try {
            this.favoritos = await this.homeS.getFavorites(null);
            console.log(this.favoritos);
        }
        catch (error) {
        }
    }
    async addFavorite(idInm) {
        try {
            const message = await this.homeS.addFavorite(idInm);
            if (message.message === 'A単adido a favoritos') {
                Swal.fire({
                    titleText: 'Susess',
                    text: message.message,
                    icon: message.icon,
                });
            }
            else {
                const { isConfirmed, isDenied } = await Swal.fire({
                    titleText: '多Estas seguro que quieres remover de tus favoritos?',
                    icon: 'question',
                    showDenyButton: true,
                    showConfirmButton: true,
                    confirmButtonText: 'Si',
                    denyButtonText: 'No'
                });
                if (isConfirmed) {
                    const removed = await this.homeS.removeFavorite(idInm);
                    Swal.fire({
                        titleText: 'Susess',
                        text: removed.message,
                        icon: removed.icon,
                    });
                }
            }
            this.loadFavorites();
        }
        catch (error) {
            Swal.fire('Error del try', 'Ocurrio un error');
        }
    }
};
HomeComponent = __decorate([
    Component({
        selector: 'app-home',
        templateUrl: './home.component.html',
        styleUrls: ['./home.component.scss']
    })
], HomeComponent);
export { HomeComponent };
