        this.cookie = cookie;
        this.router = router;
        this.homeS = homeS;
        this.activeR = activeR;
        this.fb = fb;
        this.rol = this.cookie.get('ROL');
        this.favoritos = [];
        this.name = this.cookie.get('USER');
        this.commentForm = this.fb.group({
            content: ["", [
                    Validators.required,
                    Validators.pattern(/^[a-zA-Z\s]{2,250}/)
                ]]
        });
    }
    ngOnInit() {
        this.captureParams();
        this.obtenerInmueble(this.idInm);
    }
    captureParams() {
        this.idInm = this.activeR.snapshot.params['idInm'];
    }
    async obtenerInmueble(idInm) {
        try {
            this.loadFavorites();
            const data = await this.homeS.getInmueble(idInm);
            this.inmueble = data;
        }
        catch (error) {
            Swal.fire('Ocurrio un error', undefined, 'error');
        }
    }
    noLogin() {
        if (this.rol === '') {
            this.router.navigate(['/auth/signup']);
        }
    }
    async loadFavorites() {
        if (this.rol === 'CLIENT') {
            try {
                this.favoritos = await this.homeS.getFavorites(null);
            }
            catch (error) {
            }
        }
    }
