import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { Validators } from '@angular/forms';
let NewInmbuebleComponent = class NewInmbuebleComponent {
    constructor(sanitizer, toast, fb) {
        this.sanitizer = sanitizer;
        this.toast = toast;
        this.fb = fb;
        this.imagenes = [];
        this.tags = [];
        this.extraerBase64 = async ($event) => new Promise((resolve) => {
            try {
                const unsafeImg = window.URL.createObjectURL($event);
                const image = this.sanitizer.bypassSecurityTrustUrl(unsafeImg);
                const reader = new FileReader();
                reader.readAsDataURL($event);
                reader.onload = () => {
                    resolve({
                        base: reader.result
                    });
                };
                reader.onerror = error => {
                    resolve({
                        base: null
                    });
                };
            }
            catch (e) {
                return null;
            }
        });
        this.newInmuebleForm = this.fb.group({
            type: ["", Validators.required],
            city: ["", Validators.required],
            sector: ["", Validators.required],
            description: ["", Validators.required],
            attr: this.fb.group({
                levels: [0, Validators.required],
                rooms: [0, Validators.required],
                baths: [0, Validators.required],
                garage: [0, Validators.required]
            }),
            price: [0, Validators.required],
            tags: this.fb.array([]),
            imgs: [[], Validators.required]
        });
    }
    cargarImagen(event) {
        let e = event.target.files[0];
        this.extraerBase64(e).then((image) => this.imagenes.push(image.base));
        console.log(e);
    }
    cargarTag(event) {
        event.preventDefault();
        console.log(event.target.value);
        if (this.tags.length < 10) {
            this.tags.push(event.target.value);
        }
        else {
            this.toast.warning("NO puedes agregar mas tags");
        }
        event.target.value = '';
    }
    ngOnInit() {
    }
};
