import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { Validators } from '@angular/forms';
let MyAccountComponent = class MyAccountComponent {
    constructor(sanitizer, profileS, toast, router, fb) {
        this.sanitizer = sanitizer;
        this.profileS = profileS;
        this.toast = toast;
        this.router = router;
        this.fb = fb;
        this.datos = {};
        this.isEdit = false;
        this.nProfile = null;
        this.extraerBase64 = async ($event) => new Promise((resolve) => {
            try {
                const unsafeImg = window.URL.createObjectURL($event);
                const image = this.sanitizer.bypassSecurityTrustUrl(unsafeImg);
                const reader = new FileReader();
                reader.readAsDataURL($event);
                reader.onload = () => {
                    resolve({
                        base: reader.result
                    });
                };
                reader.onerror = error => {
                    resolve({
                        base: null
                    });
                };
            }
            catch (e) {
                return null;
            }
        });
        this.editUserForm = this.fb.group({
            name: ['', Validators.required],
            lastname: ['', Validators.required],
            email: ['', Validators.required],
            password: [''],
            rol: ['', Validators.required],
            number: ['', Validators.required],
            desc: ['', Validators.required],
        });
    }
    ngOnInit() {
        this.obtenerProfile();
    }
    crearForm(data) {
        this.editUserForm.controls['name'].setValue(this.datos.name);
        this.editUserForm.controls['lastname'].setValue(this.datos.lastname);
        this.editUserForm.controls['email'].setValue(this.datos.email);
        this.editUserForm.controls['rol'].setValue(this.datos.rol);
        this.editUserForm.controls['number'].setValue(this.datos.number);
        this.editUserForm.controls['desc'].setValue(this.datos.desc);
        this.editUserForm.controls['password'].setValue(`-_-_${this.datos.password}-_-_`);
    }
    obtenerProfile() {
        this.profileS.getData(true).subscribe(data => {
            this.datos = data.user;
            this.crearForm(data);
        }, err => {
        });
    }
    touchEditar(event) {
        event.preventDefault();
        this.isEdit = true;
        this.toast.info('ahora estas editando tu perfil', 'Editar Activated');
    }
    async captarProfile(event) {
        console.log(event.target.files[0]);
        let img = await this.extraerBase64(event.target.files[0]);
        let base = img.base;
        let data = await this.getImageDimensions(base);
        if (data.w <= (data.h * 1.16)) {
            this.nProfile = base;
            if (data.w > data.h) {
                this.classImg = `transform: scale(1.2)`;
            }
            else if (data.w <= data.h) {
                this.classImg = 'width: 12rem;';
            }
        }
        else {
            this.toast.show('La imagen debe tener una relacion de aspecto proporcional 1:1, recortela o elija otra', 'DIMENSION NO PERMITIDA');
        }
    }
    noImage() {
        this.nProfile;
    }
    getImageDimensions(file) {
        return new Promise(function (resolved, rejected) {
            var i = new Image();
            i.onload = function () {
                resolved({ w: i.width, h: i.height });
            };
            i.src = file;
        });
    }
};
